import React, { Component } from 'react';
import Headline from './components/Headline';
import PokemonCard from './components/PokemonCard';
import pokemons from './data/DataPokemons';
import './App.css';

class App extends Component {

  constructor() {
    super()

    this.handleChange = this.handleChange.bind(this);

    this.state = {
      pokemontype: "",
    }
  }

  handleChange(event) {

  }

  render(){

    const { pokemonType } = this.state;

    return(
      <>
        <header>
          <Headline type="title" text="Pokedex" addClass=" " />
          <Headline type="subtitle" text="Pokemon Center" />
        </header>
        <main>
          <section>
            <ul>
              {pokemons.filter((poke) => poke.type === pokemonType)
              .map((poke) => <PokemonCard key={poke.id} named={poke.name} image={poke.image} type={poke.type} weight={poke.averageWeight} />
              )}
            </ul>
            <button onClick={this.handleChange} value="All">All</button>
            <button onClick={this.handleChange} value="Eletric">Eletric</button>
            <button onClick={this.handleChange} value="Fire">Fire</button>
            <button onClick={this.handleChange} value="Bug">Bug</button>
            <button onClick={this.handleChange} value="Psychic">Psychic</button>
            <button onClick={this.handleChange} value="Poison">Poison</button>
            <button onClick={this.handleChange} value="Normal">Normal</button>
            <button onClick={this.handleChange} value="Dragon">Dragon</button>
          </section>
        </main>
      </>
    );
  }
}

export default App;
