import React, { Component } from 'react';

class SearchArea extends Component {

  handleInput(event, handleState){
    handleState(event);
  }

  handleButton(event, handleState){
    handleState(event);
  }

  handleSubmit(event) {
    event.preventDefault();
  }

  render(){

    const { handleInput, handleButton } = this.props;

    return(
      <form onSubmit={this.handleSubmit}>
        <input type="text" 
        id="search-box" 
        name="searcher" 
        onChange={(event) => this.handleInput(event, handleInput)} 
        placeholder="  Digite o pokemon que deseja encontrar!  ">
        </input>

        <button type="submit" onClick={(event) => this.handleButton(event, handleButton)}> Poke Pesquisa </button>
      </form>
    )
  }
}

export default SearchArea;
