import React, { Component } from 'react';
import Headline from './components/Headline';
import PokemonCard from './components/PokemonCard';
import pokemons from './data/DataPokemons';
import './App.css';

class App extends Component {

  constructor() {
    super()

    this.handleClickType = this.handleClickType.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.handleClickSearch = this.handleClickSearch.bind(this);

    this.state = {
      pokemonType: "Alakazam",
      inputValue: "",
      filterArgument: "name",
    }
  }

  handleClickType(event) {
    console.log('cheguei');
    this.setState({ filterArgument: 'type' })
    const newFilter = event.target.value;
    this.setState({ pokemonType: newFilter })  
  } 

  handleClickSearch(event) {
    this.setState({ filterArgument: 'name' })
    const newSearch = this.state.inputValue;
    this.setState({ pokemonType: newSearch })
  }

  handleChange(event) {
    const newInput = event.target.value;
    this.setState({ inputValue: newInput})
  }

  handleSubmit(event) {
    event.preventDefault();
  }

  render(){

    const { pokemonType, filterArgument } = this.state;

    let pokedex = pokemons.filter((poke) => poke[filterArgument].includes(pokemonType))
    .map((poke) => <PokemonCard key={poke.id} named={poke.name} image={poke.image} type={poke.type} weight={poke.averageWeight} />
    )

    return(
      <>
        <header>
          <Headline type="title" text="Pokedex" addClass=" " />
          <Headline type="subtitle" text="Pokemon Center" />
        </header>
        <main>
          <section>
          <div>
              <button onClick={this.handleClickType} value="">All</button>
              <button onClick={this.handleClickType} value="Eletric">Eletric</button>
              <button onClick={this.handleClickType} value="Fire">Fire</button>
              <button onClick={this.handleClickType} value="Bug">Bug</button>
              <button onClick={this.handleClickType} value="Psychic">Psychic</button>
              <button onClick={this.handleClickType} value="Poison">Poison</button>
              <button onClick={this.handleClickType} value="Normal">Normal</button>
              <button onClick={this.handleClickType} value="Dragon">Dragon</button>
            </div>

            <form onSubmit={this.handleSubmit}>
              <label htmlFor="search-box">PROCURAR POKEMON</label>
              <input type="search" id="search-box" name="searcher" onChange={this.handleChange}></input>
              <button type="submit" onClick={this.handleClickSearch}> Poke Pesquisa </button>
            </form>

            <ul>
              {pokedex}
            </ul>




          </section>
        </main>
      </>
    );
  }
}

export default App;
