import React, { Component } from 'react';

class SearchTopButtons extends Component {

  constructor(){
    super()

    this.handler = this.handler.bind(this);
  }

  handleClickEvent(event){
    const { handler } = this.props;
    handler(event.target.value);
  }

  render(){
    console.log(handler);
    return(
     <div>
        <button onClick={this.handleClickEvent} value="">All</button>
        <button onClick={this.handleClickEvent} value="Eletric">Eletric</button>
        <button onClick={this.handleClickEvent} value="Fire">Fire</button>
        <button onClick={this.handleClickEvent} value="Bug">Bug</button>
        <button onClick={this.handleClickEvent} value="Psychic">Psychic</button>
        <button onClick={this.handleClickEvent} value="Poison">Poison</button>
        <button onClick={this.handleClickEvent} value="Normal">Normal</button>
        <button onClick={this.handleClickEvent} value="Dragon">Dragon</button>
      </div>
    )
  }
}

export default SearchTopButtons;
