import React, { Component } from 'react';
import PokemonCard from '../components/PokemonCard';
console.log('cheguei no arquivo async');

class DataPokemons extends Component {
  async getPokemonData (){
    const { pokemon } = this.props;
    try {
      const binaryRes = await fetch(`https://pokeapi.co/api/v2/pokemon/${pokemon}`);
      const responseJSON = await binaryRes.json();
      const resultOfItems = await responseJSON;
      console.log('cheguei no async');

      <PokemonCard name={resultOfItems.name} 
      image={resultOfItems.sprites.front_default} 
      type={resultOfItems.types[0].type.name} 
      weight={resultOfItems.weight / 10}/>

    }catch (error) {
      alert(error);
    }

  }

  render(){
    return null;
  }
}

export default DataPokemons;
