import React, { Component } from 'react';
import Headline from './components/Headline';
import PokemonCard from './components/PokemonCard';
import pokemons from './data/DataPokemons';
import './App.css';
import TopButtons from './components/TopButtons';
import SearchArea from './components/SearchArea';

class App extends Component {

  constructor() {
    super()

    this.state = {
      pokemonsList: [],
    }
  }

  handleClickType(pokemonType) {
  } 

  handleClickSearch(pokemonType) {
  }

  handleChange(inputValue) {
  }

  fetchPokemon = async (pokemonName) => {
    const requestPokemon = await fetch(`https://pokeapi.co/api/v2/pokemon/${pokemonName}`);
    const requestPokemonObject = await requestPokemon.json();
    const { name, sprites, species, stats, types, weight, id } = await requestPokemonObject;
    const pokemonProfile = {
      id: id,
      name: name,
      image: sprites.front_default,
    }
    this.setState((prevState) => ({
      pokemonsList: [...prevState, pokemonProfile],
    }));
  }

  fetchAllPokemons = async () => {
    const requestAllPokemons = await fetch('https://pokeapi.co/api/v2/pokemon/');
    const allPokemonsObjectRequest = await requestAllPokemons.json();
    const pokemonsObjectList = await allPokemonsObjectRequest.results;
    pokemonsObjectList.forEach((poke) => {
      const { name } = poke;
      this.fetchPokemon(name);
    })
  }

  componentDidMount() {
    this.fetchAllPokemons();
  }


  render(){

    const { pokemonsList } = this.state;

    return(
      <>
        <header>
          <Headline type="title" text="Pokedex" addClass=" " />
          <Headline type="subtitle" text="Pokemon Center" />
        </header>
        <main>
          <section>
          <div className="divisor"></div>
          <TopButtons handler={this.handleClickType} />
          <SearchArea handleInput={this.handleChange} handleButton={this.handleClickSearch} />
          <ul className="filter-box">
            <Headline type="subtitle3" text="Filtrar Pesquisa:" />
            <div>
             <li>NOME</li>
             <li>TIPO</li>
             <li>PESO</li>
            </div>
          </ul>
          <ul>
            {pokemonsList.map((pokemon) => {
              <li key={pokemon.id}>
                <Headline type="subtitle3" text={pokemon.name} />
                <img src={pokemon.image} alt={pokemon.name} />
              </li>
            })}
          </ul>
          </section>
        </main>
      </>
    );
  }
}

export default App;
