import React, { Component } from 'react';
import Headline from './components/Headline';
import PokemonCard from './components/PokemonCard';
import pokemons from './data/DataPokemons';
import './App.css';

class App extends Component {

  constructor() {
    super()

    this.handleClickType = this.handleClickType.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.handleClickSearch = this.handleClickSearch.bind(this);

    this.state = {
      pokemonType: "",
      inputValue: "",
      filterArgument: " ",
    }
  }

  handleClickType(event) {
    this.setState({ filterArgument: 'type' })
    const newFilter = event.target.value;
    this.setState({ pokemonType: newFilter })  
  } 

  handleClickSearch(event) {
    this.setState({ filterArgument: 'name' })
    const newSearch = this.state.inputValue;
    this.setState({ pokemonType: newSearch })
  }

  handleChange(event) {
    const newInput = event.target.value;
    this.setState({ inputValue: newInput})
  }

  handleSubmit(event) {
    event.preventDefault();
  }

  render(){

    const { pokemonType, filterArgument } = this.state;

    let pokedex = pokemons.filter((poke) => poke[filterArgument] === pokemonType)
    .map((poke) => <PokemonCard key={poke.id} named={poke.name} image={poke.image} type={poke.type} weight={poke.averageWeight} />
    )

    return(
      <>
        <header>
          <Headline type="title" text="Pokedex" addClass=" " />
          <Headline type="subtitle" text="Pokemon Center" />
        </header>
        <main>
          <section>
            <ul>
              {pokedex}
            </ul>

            <form onSubmit={this.handleSubmit}>
              <label htmlFor="search-box">Pesquise um Pokemon </label>
              <input type="search" id="search-box" name="searcher" onChange={this.handleChange}></input>
              <button type="submit" onClick={this.handleClickSearch}>PokePesquisa</button>
            </form>

            <div>
              <button onClick={this.handleClick} value="All">All</button>
              <button onClick={this.handleClick} value="Eletric">Eletric</button>
              <button onClick={this.handleClick} value="Fire">Fire</button>
              <button onClick={this.handleClick} value="Bug">Bug</button>
              <button onClick={this.handleClick} value="Psychic">Psychic</button>
              <button onClick={this.handleClick} value="Poison">Poison</button>
              <button onClick={this.handleClick} value="Normal">Normal</button>
              <button onClick={this.handleClick} value="Dragon">Dragon</button>
            </div>
          </section>
        </main>
      </>
    );
  }
}

export default App;
