import React, { Component } from 'react';
import pokemons from '../data/DataPokemons';
import Headline from './Headline';

class PokemonCard extends Component {
  render() {

    const { id, named, image, type, weight } = this.props;

    return(

      <li key={id} className="card-pokemon">
        <img src={image} alt={`Pokemon - ${named}`} />
        <Headline type="subtitle3" text={named} />
        <p>{type}</p>
        <p>Average Weight: {weight.value}{weight.measurementUnit}</p>
      </li>

    )
  }
}

export default PokemonCard;
